import React from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';

function Account() {
    const navigate = useNavigate();

    // Handle form submission
    const handleCreateAccount = (e) => {
        e.preventDefault();
        // Implement your account creation logic here
    };

    return (
        <Container>
            <LeftPanel>
                <IntroText>
                    Join the video conference <br /> using your own avatar
                </IntroText>
                <LogoImage src="/images/Ganzi1.png" alt="Logo" />
            </LeftPanel>
            <RightPanel>
                <TitleText>
                    Create your Account
                </TitleText>
                <FormContainer>
                    <form onSubmit={handleCreateAccount}>
                        <InputField type="text" placeholder="Your name" />
                        <InputField type="email" placeholder="Email Adress" />
                        <InputField type="password" placeholder="Password" />
                        <CreateAccountButton type="submit">Join GanZi</CreateAccountButton>
                        <LoginButton>이미 계정이 있으신가요? Login하기</LoginButton>
                    </form>
                </FormContainer>
            </RightPanel>
        </Container>
    );
}

const Container = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    height: 100vh;
    background-color: #B1D9DB;
`;

const LeftPanel = styled.div`
    flex: 1;
    display: flex;
    flex-direction: column;
    align-items: center;
    padding: 20px;
`;

const IntroText = styled.p`
    font-family: 'Poppins', sans-serif;
    font-size: 25px;
    font-weight: bold; /* IntroText 두껍게 설정 */
    color: #fff;
`;

const LogoImage = styled.img`
    width: 220px;
    height: 80px;
    border-radius: 30px;
`;

const RightPanel = styled.div`
    flex: 1;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    background-color: #fff;
    border-radius: 10px;
    padding: 20px;
    height: 100%;
`;

const TitleText = styled.div`
    font-family: 'Poppins', sans-serif;
    font-size: 25px;
    font-weight: bold; /* TitleText 두껍게 설정 */
    margin-bottom: 20px;
`;

const FormContainer = styled.div`
    text-align: center;
    margin-top: 30px;
    width: 100%;
`;

const InputField = styled.input`
    font-family: 'Poppins', sans-serif;
    width: 100%;
    margin: 10px 0;
    padding: 15px;
`;

const CreateAccountButton = styled.button`
    font-family: 'Poppins', sans-serif;
    font-size: 20px;
    background-color: #7CD2D7;
    color: #fff;
    padding: 10px 20px;
    border: none;
    cursor: pointer;
    border-radius: 5px;
    width: 100%;
    margin-top: 25px;
`;

const LoginButton = styled.div`
    font-family: 'Poppins', sans-serif;
    font-size: 15px;
    color: #A0A0A0;
    margin-top: 20px;
`;

export default Account;
