import React from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';

function Account() {
    const navigate = useNavigate();

    // Handle form submission
    const handleCreateAccount = (e) => {
        e.preventDefault();
        // Implement your account creation logic here
    };

    return (
        <Container>
            <LeftPanel>
                <IntroText>
                    Join the video conference using your own avatar
                </IntroText>
                <LogoImage src="/images/Ganzi1.png" alt="Logo" />
            </LeftPanel>
            <RightPanel>
                <TitleText>
                    Create your Account
                </TitleText>
                <FormContainer>
                    <form onSubmit={handleCreateAccount}>
                        <InputField type="text" placeholder="Name" />
                        <InputField type="email" placeholder="Email" />
                        <InputField type="password" placeholder="Password" />
                        <CreateAccountButton type="submit">Create Account</CreateAccountButton>
                    </form>
                </FormContainer>
            </RightPanel>
        </Container>
    );
}

const Container = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    height: 100vh;
`;

const LeftPanel = styled.div`
    flex: 1;
    display: flex;
    flex-direction: column;
    align-items: center;
    padding: 20px;
    background-color: #B1D9DB; /* 배경색 추가 */
`;

const IntroText = styled.p`
    font-family: 'Poppins', sans-serif;
    font-size: 22px;
`;

const LogoImage = styled.img`
    width: 150px;
    height: 50px;
`;

const RightPanel = styled.div`
    flex: 1;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    background-color: #B1D9DB; /* 배경색 추가 */
`;

const TitleText = styled.div`
    font-family: 'Poppins', sans-serif;
    font-size: 25px;
`;

const FormContainer = styled.div`
    text-align: center;
`;

const InputField = styled.input`
    font-family: 'Poppins', sans-serif;
    width: 100%;
    margin: 10px 0;
    padding: 10px;
`;

const CreateAccountButton = styled.button`
    font-family: 'Poppins', sans-serif;
    background-color: #7CD2D7;
    color: #fff;
    padding: 10px 20px;
    border: none;
    cursor: pointer;
`;

export default Account;
