import React, { useState } from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';

function Account() {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSignUp = () => {
    // 회원가입 로직을 구현하거나 API 호출을 수행할 수 있습니다.
    // 이 예제에서는 간단하게 입력된 데이터를 출력합니다.
    console.log('Email:', email);
    console.log('Password:', password);
  };

  return (
    <Container>
      <SidePanel>
         {/* logo-container */}
         <div className='logo-container'>
               <img src="/images/Ganzi1.png" alt="로고이미지"/>
        </div>
        <Title>Create Account</Title>
      </SidePanel>
      <FormContainer>
        <Input
          type="Name"
          placeholder="이름"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <Input
          type="email"
          placeholder="이메일"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <Input
          type="password"
          placeholder="비밀번호"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <Button onClick={handleSignUp}>Join GanZi</Button>
      </FormContainer>
    </Container>
  );
}

const Container = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  height: 100vh;
  background-color: #fff; /* 전체 배경을 흰색으로 설정 */
`;

const SidePanel = styled.div`
  background-color: #B1D9DB; /* 민트색 배경을 설정 */
  padding: 20px;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100%;
`;

const Logo = styled.img`
  max-width: 100px; /* 로고 이미지 크기 조정 */
  margin-bottom: 20px;
`;

const Title = styled.h2`
  font-family: 'Poppins', sans-serif;
  font-size: 25px;
  margin-top: 20px; /* 로고와 제목 사이의 간격을 조절합니다 */
`;

const FormContainer = styled.div`
  background-color: #ffffff; /* 회원가입 부분의 배경을 흰색으로 설정 */
  padding: 20px;
  border-radius: 10px;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.1); /* 그림자 효과 추가 */
  display: flex;
  flex-direction: column; /* 입력 칸을 세로로 배치 */
  align-items: center;
`;

const Input = styled.input`
  padding: 10px;
  margin-bottom: 10px;
  width: 300px;
  border: 1px solid #ccc;
  border-radius: 5px;
  font-family: 'Poppins', sans-serif;
  font-size: 17px;
`;

const Button = styled.button`
  padding: 10px 20px;
  background-color: #7CD2D7;
  color: #fff;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  font-family: 'Poppins', sans-serif;
  font-size: 17px;
`;

export default Account;
