import React, { useEffect, useCallback, useState } from "react";
import ReactPlayer from 'react-player';
import { useSocket } from "../providers/Socket";
import { usePeer } from "../providers/Peer";

const VideoRoomPage = () => {
    const { socket } = useSocket();
    const { peer, createOffer, createAnswer, setRemoteAns, sendStream } = usePeer();

    const [myStream, setMyStream] = useState(null);
    // const [remoteStream, setRemoteStream] = useState(null);

    const handleNewUserJoined = useCallback(async (data) => {
        const {emailId} = data;
        console.log("New user joined the room", emailId);
        const offer = await createOffer();
        socket.emit("call-user", { emailId, offer });
    }, [createOffer, socket]);

    const handleIncommingCall = useCallback(async (data) => {
        const { from, offer } = data;
        console.log("Incomming Call From", from, offer);
        const ans = await createAnswer(offer);
        socket.emit('call-accept', { emailId: from, ans });
    }, [createAnswer, socket]);

    const handleCallAccepted = useCallback(
        async (data) => {
        const { ans } = data;
        console.log('Call Got Accepted', ans);
        await setRemoteAns(ans);
    }, [setRemoteAns]
    );

    const getUserMediaStream = useCallback(async() => {
        const stream = await navigator.mediaDevices.getUserMedia({ audio: true, video: true })
        sendStream(stream);
        setMyStream(stream);
    }, [sendStream])

    useEffect(() => {
        socket.on("user-joined", handleNewUserJoined);
        socket.on("incomming-call", handleIncommingCall);
        socket.on("call-accepted", handleCallAccepted);



        return () => {
            socket.off("user-joined", handleNewUserJoined);
            socket.off("incomming-call", handleIncommingCall);
            socket.off("call-accepted", handleIncommingCall);
        }

    }, [handleIncommingCall, handleIncommingCall, handleNewUserJoined, socket]);

    useEffect(() => {
        getUserMediaStream();
    }, [getUserMediaStream])

    return(
    <div className='videoroom-page-container'>
        <h1>VideoRoom Page</h1>
        <ReactPlayer url={myStream} playing muted />
    </div>
    );
};

export default VideoRoomPage;
